import React, { Component } from 'react';

const PersonalCard = props => {
    state = {
        count: props.age,
    };
}

clickCount = (event) => {
    console.log("clicked", props.age);

    this.setState({
        count: parseInt(state.count) + 1,
    });

    return (
        <div>
            <h1>{props.firstName}, {props.lastName}</h1>
            <p>Age: {state.count}</p>
            <p>Hair Color: {this.props.hairColor}</p>
            <button onClick={(event) => { clickCount(event); }}>Birthday Button for {this.props.lastName} {this.props.firstName}</button>
        </div>
    );
}

export default PersonalCard;
