import React, { useState } from 'react';
import Password from './password';

const PersonalCard = props => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [cnPassword, setCnPassword] = useState("");
    const [firstNameError, setFirstNameError] = useState("");
    const [lastNameError, setLastNameError] = useState("");
    const [emailError, setEmailError] = useState("");
    const [passwordError, setPasswordError] = useState("");
    const [cnpasswordError, setCnPasswordError] = useState("");

    const handleFirstName = (e) => {
        setFirstName(e.target.value);
            if(e.target.value.length > 2){
                setFirstNameError("");
            }
            else if(e.target.value.length < 2 || e.target.value.length > 0){
                setFirstNameError("First Name must be at least 2 characters");
            }
    }
    const handleLastName = (e) => {
        setLastName(e.target.value);
        if(e.target.value.length < 3 && e.target.value.length > 2){
            setLastNameError("Last Name must be at least 2 characters");
        }
        else if(e.target.value.length < 2 || e.target.value.length > 0){
            setLastNameError("");
        }
    }
    const handleEmail = (e) => {
        setEmail(e.target.value);
        if(e.target.value.length < 5 || e.target.value.length > 0){
            setEmailError("Email must be at least 5 characters");
        }
        else if(e.target.value.length < 2 || e.target.value.length > 0){
            setEmailError("");
        }
    }
    const handlePassword = (e) => {
        setPassword(e.target.value);
        if(e.target.value.length < 8){
            setPasswordError("Password must be at least 8 characters");
        }
        else if(e.target.value.length > 8 || e.target.value.length < 0){
            setPasswordError("");
        }
    }
    const handleCnPassword = (e) => {
        setCnPassword(e.target.value);
        if(e.target.value.length < 8 || e.target.value.length > 0){
            setCnPasswordError("Confirm password must be at least 8 characters");
        }
        else if(e.target.value.length < 8 || e.target.value.length > 0){
            setCnPasswordError("");
        }
    }

    return (
        <form onSubmit={ (e) => e.preventDefault() }>
            <div>
                <ul>
                    <label>First Name: </label>
                    <input type="text" onChange={handleFirstName} />
                    {
                    firstNameError ?
                    <p style={{color:'red'}}>{ firstNameError }</p> :
                    ''
                }
                </ul>
                <ul>
                    <label>Last Name: </label>
                    <input type="text" onChange={handleLastName} />
                    {
                    lastNameError ?
                    <p style={{color:'red'}}>{ lastNameError }</p> :
                    ''
                }
                </ul>
                <ul>
                    <label>Email: </label>
                    <input type="text" onChange={ handleEmail } />
                    {
                    emailError ?
                    <p style={{color:'red'}}>{ emailError }</p> :
                    ''
                }
                </ul>
                <ul>
                    <label>Password: </label>
                    <input type="password" onChange={ handlePassword } />
                    {
                    passwordError ?
                    <p style={{color:'red'}}>{ passwordError }</p> :
                    ''
                }
                </ul>
                <ul>
                    <label>Confirm Password: </label>
                    <input type="password" onChange={ handleCnPassword } />
                    {
                    cnpasswordError ?
                    <p style={{color:'red'}}>{ cnpasswordError }</p> :
                    ''
                }
                </ul>
                <ul>
                    <input type="submit" value="Create User" />
                </ul>
                <div>
                    <h2>Your data form</h2>
                    <p>First Name: {firstName}</p>
                    <p>Last Name: {lastName}</p>
                    <p>Email: {email}</p>
                    <p>Password: <Password pw={password} /></p>
                    <p>Confirm Password: <Password pw={cnPassword} /></p>
                </div>
            </div>
        </form>

    );
}
export default PersonalCard;
