import React, { useState } from 'react';
import axios from 'axios';


const Pokemonapi = props => {
    const [responseDatas, setResponseData] = useState([]);


    const callPokemonapi = () => {
        axios.get(`https://pokeapi.co/api/v2/pokemon`)
            .then(res => {
                setResponseData(res.data.results);
            })
            .catch(err => {
                console.log(err);
            });
    };

    return (


        <div>
            <div>
                <button type="submit" onClick={callPokemonapi}>Fetch Pokemon</button>
            </div>
            {
                responseDatas.map((pokemons, id) => {
                    return (
                        <span>{pokemons.name}</span>
                    )
                })}
            {/* response.data.results */}
        </div>



    )
}

export default Pokemonapi;
