import React, { Component } from 'react';

class PersonalCard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            count: this.props.age,
        };
    }

    clickCount = (event) => {
        console.log("clicked", this.props.age);

        this.setState({
            count: parseInt(this.state.count)+2,
        });
    };


    render() {
        return (
            <div>
                <h1>{this.props.firstName}, {this.props.lastName}</h1>
                <p>Age: {this.state.count}</p>
                <p>Hair Color: {this.props.hairColor}</p>
                <button onClick={(event) => { this.clickCount(event); }}>Birthday Button for {this.props.lastName} {this.props.firstName}</button>
            </div>
        );
    }

}
export default PersonalCard;
