import React, { useState } from 'react';


const Color = () => {
    const [specs, setSpecs] = useState({
        color: ""
    })
    const [allBoxes, setAllBoxes] = useState([])

    const changeHandler = (event) => {
        setSpecs({
            ...specs,
            [event.target.name]: event.target.value
        })
    };
    const submitHandler = (event) => {
        event.preventDefault();
        setAllBoxes([...allBoxes, specs])
        console.log("submitHandler");
        setSpecs({
            color: ""
        })
        console.log("allBoxes");
        console.log(allBoxes);
    }

    return (
        <>
            {/* if i want to show more than one thing, I am to wrap it using empty diamond tag <> and close it</>.  */}
            <form onSubmit={(e) => {
                submitHandler(e);
            }}>
                <div>
                    <label>Color </label>
                    <input type="text" name="color" onChange={changeHandler} value={specs.color} />
                    <button type="submit">Add</button>
                </div>
                <div>
                    {allBoxes.map(box => {
                        return <div style={{ display: "flex" }}>
                            <div style={{ backgroundColor: 'green', width: "100px", height: "100px" }}>{box}
                            </div>
                        </div>
                    })}
                </div>
            </form >
        </>
    );
};
export default Color
