import React, { useState } from 'react';
import axios from 'axios';
import {
    BrowserRouter,
    useHistory,
} from "react-router-dom";

const Starwarapi = () => {
    const [selectedPerson, setSelectedPerson] = useState("");
    const [selectedPlanet, setSelectedPlanet] = useState("");
    const [selectedId, setSelectedId] = useState("")
    const [selectedData, setSelectedData] = useState("")

    const handleData = (e) => {
        setSelectedData(e.target.value)
    }
    const dataID = (e) => {
        setSelectedId(e.target.value);
    }
    const handleSubmit = (e) => {
        if (selectedData === "selectedPerson" && selectedData === "selectedId")
            axios.get(`https://swapi.dev/api/selectPerson/${selectedId}`)
                .then(response => { setSelectedPerson(response.data.results) })
        if (selectedData === "selectPlanet" && selectedData === "selectedId")
            axios.get(`https://swapi.dev/api/selectPlanet/${selectedId}`)
                .then(response => { setSelectedPlanet(response.data.results) })
    }



    <BrowserRouter>
        <div>
            <form onSubmit={handleSubmit}>
                <select Onchange={handleData}>
                    <option value={selectedPerson}>selectedPerson</option>
                    <option value={selectedPlanet}>selectedPlanet</option>
                </select>
                <input onChange={dataID} type="number" />
                <button onClick={handleSubmit}>Search</button>
            </form>
        </div>
        <switch>
            <route path="/people/:id">
                <Starwarapi />
            </route>
            <route path="/planet/:id">
                <Starwarapi />
            </route>
        </switch>
    </BrowserRouter>

}

export default Starwarapi;
