import './App.css';
import React, { useState } from 'react';
import axios from 'axios';
import {
  useHistory
} from "react-router-dom";

function App() {
  const [select, setSelect] = useState("");
  const [id, setId] = useState("");
  const [res, setRes] = useState({});
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault()
    axios.get(`https://swapi.dev/api/${select}/${id}`)
      .then((response) => {
        console.log(response);
        setRes(response.data)
      })
      .catch(err => {
        console.log(err);
      });
  }


  return (
    <div className="App">
      <form>
        <span>Search for: </span>
        <select onChange={(e) => {
          setSelect(e.target.value)
        }}>
          <option value="people">people</option>
          <option value="planets">planets</option>
        </select>
        <input type="text" value={id} onChange={(e) => {
          setId(e.target.value)
        }} />
        <input type="submit" value="Search" onClick={handleSubmit} />
      </form >
      <div>
        <div>
          <p>Name: {res["name"]}</p>
          <p>Height: {res["height"]}</p>
          <p>Hair Color: {res["hair_color"]}</p>
          <p>Skin Color: {res["skin_color"]}</p>
          <p>{res["eye_color"]}</p>
          <p>{res["birth_year"]}</p>
          <p>{res["gender"]}</p>
        </div>
        <div>
          <p>Name: {res["result.name"]}</p>
          <p>Rotation Period: {res["rotation_period"]}</p>
          <p>Orbital Period: {res["orbital_period"]}</p>
          <p>Diameter: {res["diameter"]}</p>
        </div>
      </div>
    </div >
  )
}
export default App;
