import {
    BrowserRouter,
    Link
} from "react-router-dom";

const Starwarapi = () => {
    const [selectedData, setSelectedData] = useState("");
    const [selectedId, setSelectedId] = useState("");
    const [select, setSelect] = useState("")
    const history = useHistory();

    const handleData = (e) => {
        setSelect(e.target.value)
    }
    const handleSubmit = (e) => {
        axios.get(`https://swapi.dev/api/people/" +id`)
            .then(response => { setSelectedData(response.data.results) })
        axios.get(`https://swapi.dev/api/planet/" +id`)
            .then(response => { setSelectedId(response.data.results) })
    }



    <BrowserRouter>
        <div>
            <form onSubmit={(e) =>
                history.push(`/${selectedData}/${selectedId}`)}>
                <select Onchange={handleData}>
                    <option value={people}>People</option>
                    <option value={planets}>Planets</option>
                </select>
                <input onChange />
                <button onClick={handleSubmit}>Search</button>
            </form>
        </div>
        <switch>
            <route path="/people/:id">
                <People />
            </route>
            <route path="/planet/:id">
                <Planets />
            </route>
        </switch>
    </BrowserRouter>

}

export default Starwarapi;
