import React, {Component} from 'react';

class PersonalCard extends Component {
    render() {
        return (
            <div>
                <h1>{this.props.firstName}, {this.props.lastName}</h1>
                <p>Age: {this.props.age}</p>
                <p>Hair Color: {this.props.hairColor}</p>
                <button id="counter" onClick="">Birthday Button for {this.props.lastName} {this.props.firstName}</button>
            </div>
        )
    }
    constructor(props,parentNode) {
        super(this.props.age);

        const container = document.createElement("div");

        const heading = document.createElement("h2");
        heading.innerText = props.headingText;
        container.appendChild(heading);

        const paragraph = document.createElement("a");
        paragraph.innerText = `The count is: ${this.count}`;
        container.appendChild(paragraph);

        const btn = document.createElement("button");
        btn.innerText = props.btnText || "Click to Increment";
        btn.addEventListener("click", (event) => {
            console.log(event);
            this.count++;
            console.log(this.count);
        });
        container.appendChild(btn);

        const hr = document.createElement("hr");
        container.appendChild(hr);
        parentNode.appendChild(container);
        }
    }




